import React from 'react'
// react-boostrap
import Tabs from 'react-bootstrap/Tabs'
import Tab from "react-bootstrap/Tab"
import Jumbotron from 'react-bootstrap/Jumbotron'
import Container from 'react-bootstrap/Container'
// redux
import { connect } from 'react-redux'
// styling 
import 'bootstrap/dist/css/bootstrap.min.css';
//import 'custom.scss'
// components
import Home from "./Home"
import Plans from "./Plans"
import ExistingPlans from './ExistingPlans'
import postWellInfoToJSONDb from '../ActionCreators/postWellInfoToJSONDb'


const App = ({existingPlans, saveWellInfoToReduxStoreReducer, getWellPlansFromJSONdbReducer, postWellInfoToJSONDbReducer}) => {

const plansTabHandler = () => {
  if (postWellInfoToJSONDbReducer.status === "received" /*||*/ ) {
    return false
  }
  return true
}

const plansComponentHandler = () => {
  if (existingPlans) {
    return "TBD"
  } 
  return <Plans/>
}

  return (
    <React.Fragment>
      <Jumbotron fluid className="bg-dark text-white">
        <Container>
          <h1>LandShark App (Employee Portal)</h1>
        </Container>
      </Jumbotron>
      <Tabs>
        <Tab eventKey="Home" title="Home">
          <Container>
            <Home/>
          </Container>
        </Tab>
        <Tab eventKey="Plans" title="Plans" disabled={plansTabHandler()}>
          <Container>
        {plansComponentHandler}
          </Container>
        </Tab>

      </Tabs>
    </React.Fragment>
  );
}

const mapStateToProps = ({ saveActiveWellToReduxStoreReducer, saveWellInfoToReduxStoreReducer, getWellPlansFromJSONdbReducer, postWellInfoToJSONDbReducer}) => {
  const planned = () => {
    if (saveActiveWellToReduxStoreReducer.response.selectedWell.plans.length > 0) {
      return true
    } 
    return false
  }
  return {
    saveWellInfoToReduxStoreReducer, 
    getWellPlansFromJSONdbReducer,
    postWellInfoToJSONDbReducer,
    existingPlans: planned()
  }
}

export default connect(mapStateToProps, { })(App);
