  //     </Col>
  //   </Container>
  // )
}

const mapStateToProps = ({getWellPlansFromJSONDbReducer, activeWell, getWellsFromJSONDbReducer}) => {
  return {
    getWellPlansFromJSONDbReducer,
    activeWell
  }
}

export default connect(mapStateToProps, {getWellPlansFromJSONDb})(ExistingWellPlans)
