import React, { useState, useEffect } from "react"
//bootstrap
import Container from "react-bootstrap/esm/Container";
import Form from "react-bootstrap/Form"
import Row from "react-bootstrap/Row"
import Col from "react-bootstrap/Col"
import Button from "react-bootstrap/Button"

import 'react-datasheet/lib/react-datasheet.css';
import { connect } from "react-redux"
import ReactDataSheet from 'react-datasheet';




const Plans = ({wellInfo}) => {
  const [editGrid, setEditGrid] = useState(true)
  const [someValue, setSomeValue] = useState(15)

  const initialGrid = [
    [{value: '', readOnly: true, width: '7rem'}, {value:"Measured Depth", readOnly: true, width: '7rem'}, {value:"Inclination", readOnly: true, width: '7rem'}, {value:"Azimuth", readOnly: true, width: '7rem'}, {value:"TVD", readOnly: true, width: '7rem'}, {value:"Northing", readOnly: true, width: '7rem'}, {value:"Easting", readOnly: true, width: '7rem'}],
    [{value: 1, readOnly: true}, {value: someValue, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}],
    [{value: 2, readOnly: true}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}],
    [{value: 3, readOnly: true}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}],
    [{value: 4, readOnly: true}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}],
    [{value: 5, readOnly: true}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}],
    [{value: 6, readOnly: true}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}, {value: 0, readOnly: editGrid}],
]
  const [grid, setGrid] = useState(initialGrid)

  const onCellsChanged = changes => {
    // iterate through existing grid to get a copy and not mutate current as we iterate over
    const gridNew = grid.map(row => [...row]);
    // iterate through the changes.  Each change is a cell, each cell is an object
    // with cell (previousVal), row, col, currentVal
    // mutate the value of the newGrid[row#][col#] to the new value 
    changes.forEach(({ cell, row, col, value }) => {

      gridNew[row][col] = { ...grid[row][col], value };
    });
    setGrid(gridNew)
  };

  const addNewRow = () => {
    const localGrid = [{value: grid.length + 1, readOnly: true}, {value: 0}, {value: 0}, {value: 0}, {value: 0}, {value: 0}, {value: 0}]
    setGrid([...grid, localGrid])
  }

  const editPlans = () => {
    setEditGrid(!editGrid)
    const gridNew = grid.map(row => [...row]);
    // console.log(gridNew[0][2])
    for (let row = 1; row < gridNew.length; row ++) {
      for (let col = 1; col < gridNew[row].length; col ++) {
        gridNew[row][col].readOnly = editGrid
      }
    }
    gridNew.forEach((row) => {
      // console.log(row)
      Object.values(row).forEach(cell => {
        if(cell.row !== 0 && cell.col !== 0) {
          // console.log(cell)
          cell.readOnly = editGrid
        }
      })
    })
    setGrid(gridNew)
  }

  const removeRow = () => {
    const newGrid = [...grid]
    newGrid.pop()
    setGrid(newGrid)
  }

  useEffect(()=> {
    // console.log(`Grid Length: ${grid.length}`)
  },[grid])



  return (
    <Container>
      <Col xs={30}>{wellInfo}</Col>
      <Col xs={30}>
    <ReactDataSheet
        data={grid}
        valueRenderer={(cell) => cell.value}
        onCellsChanged={onCellsChanged}
        // editPlans={(cell) => {
        //   setEditGrid(!editGrid)
        //   cell.readOnly = editGrid
        // }}

    />
    </Col>
    <Button variant="primary" className="mt-4 mb-4" onClick={()=>addNewRow()}>Add a Row</Button>
    <Button variant="danger" className="my-4 ml-4" onClick={()=>removeRow()}>Remove a Row</Button>
    <Button variant="info" className="my-4 ml-4" onClick={()=>editPlans()}>Edit Plans</Button>
    </Container>
  )
}

const mapStateToProps = ({selectedWell}) => {

  const currentWell = Object.values(selectedWell) 
  const wellInfo = `${currentWell[0].operator} - ${currentWell[0].rig} - ${currentWell[0].well}`

  return {wellInfo}
}

export default connect(mapStateToProps)(Plans)
