import _ from "lodash";
import _default from "react-bootstrap/esm/FormControl"
import { CREATE_NEW_WELL, FETCH_WELLS, SET_ACTIVE_WELL } from "../ActionCreators/types"

const INITIAL_STATE = {
  response: {},
  status: '',
}

export default (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case CREATE_NEW_WELL:
      return {...state, response: action.payload, status:"received"}
    case FETCH_WELLS:
      return {..._.mapKeys(action.payload, 'id'), status:"received"}
    case SET_ACTIVE_WELL:
      console.log(action.payload)
      return {response: action.payload}
    default:
      return state
  }
}
