import React from 'react'
// react-boostrap
import Tabs from 'react-bootstrap/Tabs'
import Tab from "react-bootstrap/Tab"
import Jumbotron from 'react-bootstrap/Jumbotron'
import Container from 'react-bootstrap/Container'
// redux
import { connect } from 'react-redux'
// styling 
import 'bootstrap/dist/css/bootstrap.min.css';
//import 'custom.scss'
// components
import Home from "./Home"
import Plans from "./Plans"
import ExistingWellPlans from './ExistingWellPlans'


const App = ({wells, activeWell = null}) => {

  const PlansTabHandler = () => {
    if (activeWell.status === "received") {
      return false
    } /* else if (activeWell_newlyCreated.status === "recevied") {
      return false
    }*/
    return true
  }
  const renderPlansTab = () => {
    console.log(activeWell.payload.plans.length)
    if (activeWell.payload === undefined) {
      return <Plans/>
    } else if (activeWell.payload.plans.length > 0) {
      return <ExistingWellPlans/>
    } else {
      return <Plans/>
    }
  }

  return (
    <React.Fragment>
      <Jumbotron fluid className="bg-dark text-white">
        <Container>
          <h1>LandShark App (Employee Portal)</h1>
        </Container>
      </Jumbotron>
      <Tabs>
        <Tab eventKey="Home" title="Home">
          <Container>
            <Home/>
          </Container>
        </Tab>
        <Tab eventKey="Plans" title="Plans" disabled={PlansTabHandler()}>
          <Container>
            {renderPlansTab()}
          </Container>
        </Tab>

      </Tabs>
    </React.Fragment>
  );
}

const mapStateToProps = ({wells, activeWell}) => {
  return {wells, activeWell}
}

export default connect(mapStateToProps)(App);
