import React, { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

import UserPool from "./UserPool"



const SignUp = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userSubmittedCredentials, setSubmittedCreds] = useState(false)
  const [verificationCode, setVerificationCode] = useState(null)

  const submitNewUser = event => {
    // event.preventDefault();

    UserPool.signUp(email, password, [], null, (err, data) =>{
      if (err) {
        console.log(err)
      } else {
        console.log(data)
        setSubmittedCreds(true)
      }
    })
  }

  const signUp = () => {
    return (
      <Col>
        <h3 className="my-4">Sign Up</h3>
        <Form.Control className="mt-3" placeholder="Enter Email" onChange={event => setEmail(event.target.value)} />
        <Form.Control className="mt-3" placeholder="Create Password" onChange={event => setPassword(event.target.value)} />
        <Button className="mt-4" variant="info" disabled={ email === "" || password === "" ? true : false } onClick={()=>submitNewUser()}>Submit</Button>
      </Col>
    )
  }

  const confirmationCode = () => {
     return (
      <Col>
        <h3 className="my-4">Confimation Code</h3>
        <Form.Control className="mt-3" placeholder="Enter Verification Code" onChange={event => setSubmittedCreds(event.target.value)} />
        <Button className="mt-4" variant="info" disabled={ verificationCode === "" ? true : false }>Submit</Button>
      </Col>
     )
  }

  return (
    <Container>
      <Col>
        {signUp()}
        {userSubmittedCredentials ? confirmationCode() : null }
      </Col>
    </Container>
  )

}

export default SignUp
