import { SET_ACTIVE_WELL } from "../ActionCreators/types"

const INITIAL_STATE = {
  response: {},
  status: '',
}


export default (state = INITIAL_STATE, {type, payload}) => {
  switch (type) {
    case SET_ACTIVE_WELL:
      return {[payload.id]: payload, status: 'Hallelueh' };
    default:
      return state;
  }
};
