import React, { useEffect, useState } from "react"
import _ from "lodash";
import saveWellInfoToReduxStore from "../ActionCreators/saveWellInfoToReduxStore"
import fetchWellsFromJSON from "../ActionCreators/fetchWellsFromJSON"

// react-bootstrap
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import DropdownButton from 'react-bootstrap/DropdownButton'
import Form from 'react-bootstrap/Form';
import Dropdown from 'react-bootstrap/Dropdown';
import { connect } from "react-redux";


const Home = ({saveWellInfoToReduxStore, fetchWellsFromJSON, wells}) => {
  const [operator, setOperator] = useState('')
  const [rig, setRig] = useState('')
  const [well, setWell] = useState('')
  const [county, setCounty] = useState('')
  const [state, setState] = useState('')
  const [SHL_Northing, setSHL_Northing] = useState('')
  const [SHL_Easting, setSHL_Easting] = useState('')
  const [selectedWell, setSelectedWell] = useState(null)

  useEffect(()=>{
    fetchWellsFromJSON()
  },[])

  const handleNewWellSubmit = () => {
    const wellInfo = {
      operator,
      rig,
      well,
      county,
      state,
      SHL_Northing,
      SHL_Easting,
    }
    saveWellInfoToReduxStore(wellInfo)
  }

  const renderWellNames = () => {
    const renderedWells = wells.map(well => {
      if (!well.operator) {
        return null
      }
    return <Dropdown.Item href="" key={well.well}>{`${well.operator} - ${well.rig} - ${well.well}`}</Dropdown.Item>
    })
    return (
      <Dropdown.Menu>
        {renderedWells}
      </Dropdown.Menu>
    )
  }


  const selectExistingWell = () => {
    return (
      <Col>
      <h3 className="my-4">Select Existing Well</h3>
      <Dropdown>
        <Dropdown.Toggle variant="primary" id="dropdown-basic">
          Selected Well: {"tbd"}
        </Dropdown.Toggle>
        {renderWellNames()}
      </Dropdown>
    </Col>
    )
  }


const inputNewWell = () => {
  return (
    <Col md={4}>
      <h3 className="my-4"> Add New Well Data</h3>
      <Form.Control className="mt-3" placeholder="Operator" onChange={e => setOperator(e.target.value)}/>
      <Form.Control className="mt-3" placeholder="Rig" onChange={e => setRig(e.target.value)}/>
      <Form.Control className="mt-3" placeholder="Well" onChange={e => setWell(e.target.value)}/>
      <Form.Control className="mt-3" placeholder="County" onChange={e => setCounty(e.target.value)}/>
      <Form.Control className="mt-3" placeholder="State" onChange={e => setState(e.target.value)}/>
      <Form.Control className="mt-3" placeholder="Northing" onChange={e => setSHL_Northing(e.target.value)}/>
      <Form.Control className="mt-3" placeholder="Easting" onChange={e => setSHL_Easting(e.target.value)}/>
      <Button variant="primary" className="mt-4 mb-4" onClick={handleNewWellSubmit}>Create New Well</Button>
      {/* <Button variant="primary" className="mt-4 ml-4 mb-4" onClick={()=>console.log(wells)}>Get list of wells</Button> */}
    </Col>
  )
}
console.log(wells)
  return (
    <Container>
      <Row>
        {inputNewWell()}
        {selectExistingWell()}
      </Row>
    </Container>
  )
}

const mapStateToProps = (state) => {
  let wellInfo = Object.values(state.wells)

  // for (let well in state.wells) {
  //   console.log(well)
  // }

  return {wells: Object.values(state.wells)}
}

export default connect(mapStateToProps, {saveWellInfoToReduxStore, fetchWellsFromJSON})(Home)
