import React, { useState } from "react"
//bootstrap
import Container from "react-bootstrap/esm/Container";
import Form from "react-bootstrap/Form"
import Row from "react-bootstrap/Row"
import Col from "react-bootstrap/Col"

import 'react-datasheet/lib/react-datasheet.css';
import { connect } from "react-redux"
import ReactDataSheet from 'react-datasheet';




const Plans = () => {
    const initialGrid = [
      [{value: 'Plan #', readOnly: true, width: '3rem'}, {value:"Measured Depth", readOnly: true, width: '7rem'}, {value:"Inclination", readOnly: true, width: '7rem'}, {value:"Azimuth", readOnly: true, width: '7rem'}, {value:"TVD", readOnly: true, width: '7rem'}, {value:"Northing", readOnly: true, width: '7rem'}, {value:"Easting", readOnly: true, width: '7rem'}],
      [{value: 1, readOnly: true}, {value: 0, readOnly: true}, {value: 0, readOnly: true}, {value: 0, readOnly: true}, {value: 0, readOnly: true}, {value: 0, readOnly: true}, {value: 0, readOnly: true}],
      [{value: 2, readOnly: true}, {value: 0}, {value: 0}, {value: 0}, {value: 0}, {value: 0}, {value: 0}],
      [{value: 3, readOnly: true}, {value: 0}, {value: 0}, {value: 0}, {value: 0}, {value: 0}, {value: 0}],
      [{value: 4, readOnly: true}, {value: 0}, {value: 0}, {value: 0}, {value: 0}, {value: 0}, {value: 0}],
      [{value: 5, readOnly: true}, {value: 0}, {value: 0}, {value: 0}, {value: 0}, {value: 0}, {value: 0}],
      [{value: 6, readOnly: true}, {value: 0}, {value: 0}, {value: 0}, {value: 0}, {value: 0}, {value: 0}]
  ]
  const [grid, setGrid] = useState(initialGrid)

  const onCellsChanged = changes => {
    // iterate through existing grid to get a copy and not mutate current as we iterate over
    const gridNew = grid.map(row => [...row]);
    // iterate through the changes.  Each change is a cell, each cell is an object
    // with cell (previousVal), row, col, currentVal
    // mutate the value of the newGrid[row#][col#] to the new value 
    changes.forEach(({ cell, row, col, value }) => {
      gridNew[row][col] = { ...grid[row][col], value };
    });
    setGrid(gridNew)
  };



  return (
    <Container>
      <Col xs={10}>
    <ReactDataSheet
        data={grid}
        valueRenderer={(cell) => cell.value}
        onCellsChanged={onCellsChanged}
    />
    </Col>
    </Container>
  )
}

export default connect(null)(Plans)
