import React, { useEffect, useState } from "react"
import saveWellInfoToReduxStore from "../ActionCreators/saveWellInfoToReduxStore"
import fetchWellsFromJSON from "../ActionCreators/fetchWellsFromJSON"

// react-bootstrap
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Dropdown from 'react-bootstrap/Dropdown';
import { connect } from "react-redux";


const Home = ({saveWellInfoToReduxStore, fetchWellsFromJSON, getWellsFromReducer}) => {
  const [operator, setOperator] = useState('')
  const [rig, setRig] = useState('')
  const [well, setWell] = useState('')
  const [county, setCounty] = useState('')
  const [state, setState] = useState('')
  const [SHL_Northing, setSHL_Northing] = useState('')
  const [SHL_Easting, setSHL_Easting] = useState('')



  const handleNewWellSubmit = () => {
    const wellInfo = {
      operator,
      rig,
      well,
      county,
      state,
      SHL_Northing,
      SHL_Easting,
    }
    console.log(wellInfo)
    saveWellInfoToReduxStore(wellInfo)
  }

  const renderWellNames = () => {
    return (
      <Dropdown.Menu>
        {getWellsFromReducer.map(well => {
          return (
            <Dropdown.Item href="">
              {well.operator - well.rig - well.well}
            </Dropdown.Item>
          )
        })}
      </Dropdown.Menu>
    )
  }



  return (
    <Container>
    <Col md={6}>
      <h3 className="my-4"> Add New Well Data</h3>
      <Form.Control className="mt-3" placeholder="Operator" onChange={e => setOperator(e.target.value)}/>
      <Form.Control className="mt-3" placeholder="Rig" onChange={e => setRig(e.target.value)}/>
      <Form.Control className="mt-3" placeholder="Well" onChange={e => setWell(e.target.value)}/>
      <Form.Control className="mt-3" placeholder="County" onChange={e => setCounty(e.target.value)}/>
      <Form.Control className="mt-3" placeholder="State" onChange={e => setState(e.target.value)}/>
      <Form.Control className="mt-3" placeholder="Northing" onChange={e => setSHL_Northing(e.target.value)}/>
      <Form.Control className="mt-3" placeholder="Easting" onChange={e => setSHL_Easting(e.target.value)}/>
      <Button variant="primary" className="mt-4" onClick={handleNewWellSubmit}>Create New Well</Button>
      <Button variant="primary" className="mt-4 ml-4" onClick={()=>fetchWellsFromJSON()}>Get list of wells</Button>
    </Col>
    <Col md={6}>
      here
    </Col> 
    </Container>
  )
}

const mapStateToProps = (state) => {
  return {getWellsFromReducer: Object.values(state.wells)}
}

export default connect(mapStateToProps, {saveWellInfoToReduxStore, fetchWellsFromJSON})(Home)
