import React, {useState} from "react"
import Container from "react-bootstrap/esm/Container"
import SignIn from "./SignIn"
import SignUp from "./SignUp"
import { connect } from "react-redux"
import userNeedsToCreateAccount from "../../ActionCreators/UserVerification/userNeedsToCreateAccount.js"

const SignInHome = ({userNeedsToCreateAccount}) => {
  // const [existingUser, setExistingUser] = useState(true)
  const SignInOrSignUp = () => {
    if (userNeedsToCreateAccount.response) {
      console.log("user needs an account -sent from Home")
      return (
        <Container>
          <SignUp/>
        </Container>
      )
    }
    return (
      <Container>
        <SignIn/>
      </Container>
    )
  }
  return (
    SignInOrSignUp()
  )
}

const mapStateToProps = ({ userNeedsToCreateAccount }) => {
  return {
    userNeedsToCreateAccount
  }
}

export default connect(mapStateToProps)(SignInHome);
