import { POST_PLANS_TO_DYNAMODB_REQUESTED,
  POST_PLANS_TO_DYNAMODB_RECEIVED,
  POST_PLANS_TO_DYNAMODB_FAILED } from "../ActionCreators/types"

const INITIAL_STATE = {
  response: {},
  status: ""
}
export default (state = INITIAL_STATE, {type, payload}) => {
  switch (type) {
    case POST_PLANS_TO_DYNAMODB_REQUESTED:
      return {...state, status: "waiting"}
    case POST_PLANS_TO_DYNAMODB_RECEIVED:
      return {...state, response: payload, plans: wellInfo.planRecords ,status: "received"}
    case POST_PLANS_TO_DYNAMODB_FAILED:
      return {...state, response: payload, status: "failed"}
    default:
      return state;
  }
}
