import { SET_ACTIVE_WELL } from "../ActionCreators/types"

// const INITIAL_STATE = {
//   response: {},
//   status: '',
// }

export default (state={}, action) => {
  console.log("Here at the selected Well Reducer")
  switch (action.type) {
    case SET_ACTIVE_WELL:
      return {...state, action}
    default:
      return state;
  }
}
