import { SAVE_WELL_PLANS_TO_JSON } from "../ActionCreators/types"

const INITIAL_STATE = {
  reponse: {},
  status: ""
}

export default (state=INITIAL_STATE, {type, payload}) => {
  switch (type) {
    case SAVE_WELL_PLANS_TO_JSON:
      return {...state, response: payload, status: "received"}
    default:
      return state
  }
}
