import _ from "lodash";
import _default from "react-bootstrap/esm/FormControl"
import { CREATE_NEW_WELL, FETCH_WELLS, SET_ACTIVE_WELL, SAVE_NEW_WELL_TO_REDUX_STORE } from "../ActionCreators/types"

const INITIAL_STATE = {
  response: {},
  status: '',
}

export default (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case CREATE_NEW_WELL:
      return {...state, response: action.payload, status:"received"}
    case FETCH_WELLS:
      return {..._.mapKeys(action.payload, "operator".concat("well")), status:"received"}
    case SAVE_NEW_WELL_TO_REDUX_STORE:
      return {...state, response: action.payload, status: "newWell"}
    default:
      return state
  }
}
