import { SET_ACTIVE_WELL } from "../ActionCreators/types"

const INITIAL_STATE = {
  response: {},
  status: '',
}

// export default (state={}, {type, payload}) => {
//   console.log("Here at the selected Well Reducer")
//   switch (type) {
//     case SET_ACTIVE_WELL:
//       console.log('Active well reducer, case: active well reached')
//       return {...state, payload}
//     default:
//       console.log('didnt make it to case1')
//       return state;
//   }
// }

export default (state = INITIAL_STATE, { type, payload }) => {
  switch (type) {
    case SET_ACTIVE_WELL:
      console.log("made it to the switch")
      return { ...state, status: 'waiting' };
    default:
      return state;
  }
};
